    74	fn press_enter_to_exit(exit_code: i32) {
    75	    print!("Press Enter to exit...");
    76	    io::stdout().flush().unwrap();
    77	    let mut buffer = String::new();
    78	    io::stdin().read_line(&mut buffer).unwrap();
    79	    std::process::exit(exit_code);
    80	}
