   283	fn get_random_hex(length: usize) -> String {
   284	    thread_rng()
   285	        .sample_iter(&Alphanumeric)
   286	        .take(length)
   287	        .map(char::from)
   288	        .collect::<String>()
   289	        .to_lowercase() // PowerShell version produces lowercase hex
   290	}
